<script lang="ts">
    //import JSZip from 'jszip';
    import { SelectedArchiveEntry } from './stores';
    import { AvailableMimeTypes, ExtensionMimeTypes } from './mimes';
    import { prettyXML } from './pretty';

    export let archive: File | null = null;

    $: promise = extractArchiveEntries(archive);

    async function extractArchiveEntries(archive: File | null): Promise<File[]> {
        // TODO: make thread safe ...
        SelectedArchiveEntry.set(null);
        if(!archive) {
            return [];
        }
        let entries: any[] = [];
        let zip = await JSZip.loadAsync(archive, {});
        zip.forEach((_: any, entry: any) => {
            if(!entry.dir) {
                entries.push(entry);
            }
        });
        let result: File[] = [];
        for(let entry of entries) {
            let data: Blob = await entry.async('blob');
            const extension: string = entry.name.split('.').pop();
            result.push(new File([data], entry.name, { type: data.type || ExtensionMimeTypes[extension] || AvailableMimeTypes.bin }));
        }
        return result;
    }

    async function download(file: File) {
        const a = document.createElement('a');
        let downloadFile = file;
        switch(file.type) {
            case AvailableMimeTypes.json:
                let json = JSON.parse(await file.text());
                if(json.Content && json.Content.Data) {
                    downloadFile = new File([prettyXML(json.Content.Data)], file.name + '.content.xml', { type: AvailableMimeTypes.xml });
                } else {
                    downloadFile = new File([JSON.stringify(json, null, 2)], file.name, { type: file.type });
                }
                break;
            case AvailableMimeTypes.xml:
                downloadFile = new File([prettyXML(await file.text())], file.name, { type: file.type });
                break;
            default:
                downloadFile = file;
        }
        a.href = URL.createObjectURL(downloadFile);
        a.download = downloadFile.name;
        a.click();
        // TODO: free object URL after download complete ...
    }
</script>

<style>
    .loader {
        display: block;
        margin-top: 1em;
        margin-bottom: 1em;
        margin-left: auto;
        margin-right: auto;
    }

    .container {
        height: 100%;
        display: block;
        overflow-y: scroll;
        background-color: white;
        min-height: 3em;
        user-select: none;
    }

    .error {
        color: red;
        padding: 0.25em;
    }

    .row {
        display: flex;
        font-size: small;
        transition: background-color 333ms;
        vertical-align: middle;
    }

    .row:hover {
        background-color: rgba(0, 128, 255, 0.25);
    }

    .selected {
        background-color: rgba(0, 128, 255, 0.5) !important;
    }

    .file {
        flex: 1;
        padding-left: 0.25em;
        padding-right: 0.25em;
        /*vertical-align: middle;*/
        line-height: 1.5em;
        overflow-x: hidden;
        white-space: nowrap;
        text-overflow: ellipsis;
    }

    .download {
        flex: 0;
        float: right;
        visibility: hidden;
        padding-left: 0.25em;
        padding-right: 0.25em;
        opacity: 0;
        cursor: pointer;
        transition: opacity 333ms;
    }

    .row:hover .download {
        visibility: visible;
        opacity: 1;
    }
</style>

<main class="container">
    {#await promise}
        <img class="loader" alt="Extracting..." title="Extracting Archive ..." src="data:image/svg+xml;base64," />
    {:then files}
        {#each files as file}
            <div class="row" class:selected={$SelectedArchiveEntry === file}>
                <div class="file" title={file.name} on:click={() => SelectedArchiveEntry.set(file)}>{file.name}</div>
                <div class="download" title="Download the (embedded) content of the file" on:click={() => download(file)}>ðŸ’¾</div>
            </div>
        {/each}
    {:catch error}
        <p class="error">{error.message}</p>
    {/await}
</main>